import { useState } from 'react';
import axios from 'axios';
import CGLogo from './chatGPT.png';
import AppLogo from './app-logo.png';
import './App.css';

function App() {
  const [prompt, setPrompt] = useState('');
  const [response, setResponse] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);

    axios
      .post('http://localhost:5555/chat', { prompt })
      .then((res) => {
        setResponse(res.data);
        setLoading(false);

        // Create a Blob with the project code
        const projectCode = new Blob([res.data], { type: 'text/plain' });

        // Create a download link
        const downloadLink = URL.createObjectURL(projectCode);

        // Trigger the download
        const link = document.createElement('a');
        link.href = downloadLink;
        link.download = 'project.js';
        link.click();

        setResponse('Project code received. Click the link to download.');
      })
      .catch((err) => {
        console.error(err);
      });
  };

  return (
    <div className="wrapper">
      <img src={AppLogo} alt="" className="app-logo" />
      <form onSubmit={handleSubmit}>
        <img src={CGLogo} alt="" className={loading ? 'cg-logo loading' : 'cg-logo'} />
        <input
          type="text"
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          placeholder="Ask anything... :)"
        />
        <button type="submit">Ask</button>
      </form>
      <p className="response-area">{loading ? 'loading...' : response}</p>
      <div className="footer">~ webstylepress ~</div>
    </div>
  );
}

export default App;
