import { useState } from 'react';
import axios from 'axios';
import CGLogo from './chatGPT.png';
import AppLogo from './app-logo.png';
import './App.css';
import JSZip from 'jszip';

const App = () => {
  const [inputs, setInputs] = useState([]);
  const [selectedLanguages, setSelectedLanguages] = useState([]);
  const [selectedFrameworks, setSelectedFrameworks] = useState([]);
  const [selectedVersions, setSelectedVersions] = useState([]);
  const [selectedLibraries, setSelectedLibraries] = useState([]);
  const [selectedProfile, setSelectedProfile] = useState("");

  const [prompt, setPrompt] = useState('');

  const [response, setResponse] = useState('');
  const [loading, setLoading] = useState(false);


  const handleProfileSelection = (profile) => {
    setSelectedProfile(profile);
  };


  const handleAddInput = () => {
    setInputs([...inputs, ""]);
  };

  const handleDeleteInput = (index) => {
    const updatedInputs = [...inputs];
    updatedInputs.splice(index, 1);
    setInputs(updatedInputs);
  };

  const handleInputChange = (index, value) => {
    const updatedInputs = [...inputs];
    updatedInputs[index] = value;
    setInputs(updatedInputs);
  };

  const handleLanguageClick = (language) => {
    if (selectedLanguages.includes(language)) {
      setSelectedLanguages(
        selectedLanguages.filter((lang) => lang !== language)
      );
      setSelectedFrameworks([]);
      setSelectedVersions([]);
      setSelectedLibraries([]);
    } else {
      setSelectedLanguages([...selectedLanguages, language]);
    }
  };

  const handleFrameworkClick = (framework) => {
    if (selectedFrameworks.includes(framework)) {
      setSelectedFrameworks(
        selectedFrameworks.filter((fw) => fw !== framework)
      );
      setSelectedVersions([]);
      setSelectedLibraries([]);
    } else {
      setSelectedFrameworks([...selectedFrameworks, framework]);
    }
  };

  const handleVersionClick = (version) => {
    if (selectedVersions.includes(version)) {
      setSelectedVersions(selectedVersions.filter((ver) => ver !== version));
    } else {
      setSelectedVersions([...selectedVersions, version]);
    }
  };

  const handleLibraryClick = (library) => {
    if (selectedLibraries.includes(library)) {
      setSelectedLibraries(selectedLibraries.filter((lib) => lib !== library));
    } else {
      setSelectedLibraries([...selectedLibraries, library]);
    }
  };

  const updatePromptValue = () => {
    const promptValue = `Act as a highly experienced and seasoned :${selectedProfile} in the field .Build a: ${inputs.slice(0)}. Your goal is to generate a code handling the following tasks: ${inputs.slice(1).join(', ')} in: ${selectedLanguages.join(', ')} using: ${selectedFrameworks.join(', ')} with :${selectedVersions.join(', ')} and ${selectedLibraries.join(', ')}.
Then, act as a bug detector and review your code for security vulnerabilities and locate any logic errors or resource leaks and add the fixes needed.
Then, act as a code reviewer and analyze the whole previous provided code for code smells and assess the test coverage and add improvements to your code.
Then, act as a bug detector and review my code for security vulnerabilities and locate any logic errors and add the fixes needed.
Responses should not be translations of my input, but code written in the language I specified. Do not write explanations in your reply. Keep in mind that it is important to be concise, specific, and straight to the point.`;
    setPrompt(promptValue);
  };

  const generateCodeFiles = async () => {
    setLoading(true);
    setResponse('');

    try {
      // Make a POST request to the API endpoint
      const response = await axios.post('/api/generate-code', {
        prompt,
      });

      // Check if the response is successful
      if (response.status === 200) {
        const { codeFiles } = response.data;

        // Create a zip file using JSZip
        const zip = new JSZip();

        // Add each code file to the zip
        codeFiles.forEach((file) => {
          zip.file(file.name, file.content);
        });

        // Generate the zip file
        const content = await zip.generateAsync({ type: 'blob' });

        // Create a temporary link to download the zip file
        const link = document.createElement('a');
        link.href = URL.createObjectURL(content);
        link.download = 'code-files.zip';
        link.click();

        // Clean up the temporary link
        URL.revokeObjectURL(link.href);

        // Set the response message
        setResponse('Code files generated successfully!');
      }
    } catch (error) {
      // Handle any errors that occur during the API request
      setResponse('Error generating code files.');
    }

    setLoading(false);
  };


  const createFile = (folder, fileName, code) => {
    folder.file(fileName, code);
  };

  const generateAndDownloadFiles = async () => {
  setLoading(true);
  updatePromptValue();

  try {
    const code = await generateCodeFiles();
    const folderName = 'GeneratedFolder';
    const files = [
      { name: 'file1.js', code: code },
      // Add more files here if needed
    ];

    const zip = new JSZip();
    const folder = zip.folder(folderName);

    files.forEach((file) => {
      const fileName = `${file.name}`;
      createFile(folder, fileName, file.code);
    });

    const zipBlob = await zip.generateAsync({ type: 'blob' });
    const zipDownloadLink = URL.createObjectURL(zipBlob);

    const link = document.createElement('a');
    link.href = zipDownloadLink;
    link.download = `${folderName}.zip`;
    document.body.appendChild(link); // Append the link element to the DOM
    link.click();
    document.body.removeChild(link); // Remove the link element from the DOM after the download

    setResponse('Project files received. Click the link to download.');
  } catch (err) {
    console.error(err);
  }

  setLoading(false);
};


  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const res = await axios.post('http://localhost:5555/chat', { prompt });
      setResponse(res.data);
      setLoading(false);

      // Call the function to generate and download the files
      generateAndDownloadFiles();
    } catch (err) {
      console.error(err);
    }
  };

  // Rest of the code remains the same



  const programmingLanguages = [
    {
      name: "JavaScript",
      frameworks: [
        {
          name: "React",
          versions: ["17.0.2", "16.13.1"],
          libraries: ["React-DOM", "Redux"],
        },
        {
          name: "Angular",
          versions: ["12.0.0", "11.2.14"],
          libraries: ["RxJS", "NgRx"],
        },
      ],
    },
    {
      name: "Python",
      frameworks: [
        {
          name: "Django",
          versions: ["3.2.4", "2.2.24"],
          libraries: ["Django REST Framework", "Celery"],
        },
        {
          name: "Flask",
          versions: ["2.0.1", "1.1.4"],
          libraries: ["Flask-RESTful", "SQLAlchemy"],
        },
      ],
    },
    {
      name: "Java",
      frameworks: [
        {
          name: "Spring Boot",
          versions: ["2.5.0", "2.4.6"],
          libraries: ["Spring Data JPA", "Spring Security"],
        },
        {
          name: "Hibernate",
          versions: ["5.5.3", "5.4.31"],
          libraries: ["Java Persistence API (JPA)", "C3P0"],
        },
      ],
    },
  ];

  return (
    <div className="wrapper">
      <img src={AppLogo} alt="" className="app-logo" />
      <form onSubmit={handleSubmit}>
        <div>
        <img src={CGLogo} alt="" className={loading ? 'cg-logo loading' : 'cg-logo'} />
        <select
          style={{ width: "1300px" }}
          value={selectedProfile}
          onChange={(e) => handleProfileSelection(e.target.value)}
        >
          <option value="">Select a profile</option>
          <option value="Front-end Developer">Front-end Developer</option>
          <option value="Back-end Developer">Back-end Developer</option>
          <option value="Full-stack Developer">Full-stack Developer</option>
          <option value="Mobile App Developer">Mobile App Developer</option>
          <option value="Game Developer">Game Developer</option>
          <option value="DevOps Engineer">DevOps Engineer</option>
          <option value="Quality Assurance (QA) Engineer">
            Quality Assurance (QA) Engineer
          </option>
          <option value="UI/UX Developer">UI/UX Developer</option>
          <option value="Data Engineer">Data Engineer</option>
          <option value="Machine Learning Engineer">
            Machine Learning Engineer
          </option>
        </select>

        <div style={{ display: "flex", alignItems: "center" }}>
        <input
          type="text"
          value={inputs[0] || ""}
          placeholder="Provide your Main Task"
          onChange={(e) => handleInputChange(0, e.target.value)}
          style={{ marginRight: "65px" }}
        />
      </div>

      {inputs.slice(1).map((input,index) => (
        <div key={index + 1} style={{ display: "flex", alignItems: "center" }}>
          <input
            type="text"
            value={input}
            placeholder="Add a functionality"
            onChange={(e) => handleInputChange(index + 1, e.target.value)}
            style={{ marginRight: "10px" }}
          />
          <button onClick={() => handleDeleteInput(index + 1)}>Delete</button>
        </div>
      ))}

        <button onClick={handleAddInput}>Add a functionality</button>
      </div>

      <div>
        {/* Display programming languages */}
        <h3>Programming Languages</h3>
        {programmingLanguages.map((language) => (
          <button
            key={language.name}
            onClick={() => handleLanguageClick(language.name)}
            style={{
              marginRight: "10px",
              marginBottom: "10px",
              backgroundColor: selectedLanguages.includes(language.name)
                ? "lightblue"
                : "#ccc",
              color: "#fff",
            }}
          >
            {language.name}
          </button>
        ))}
      </div>

      {selectedLanguages.map((selectedLanguage) => {
        const language = programmingLanguages.find(
          (lang) => lang.name === selectedLanguage
        );

        return (
          <div key={selectedLanguage}>
            <h3>Frameworks for {selectedLanguage}</h3>
            {language.frameworks.map((framework) => (
              <button
                key={framework.name}
                onClick={() => handleFrameworkClick(framework.name)}
                style={{
                  marginRight: "10px",
                  marginBottom: "10px",
                  backgroundColor: selectedFrameworks.includes(framework.name)
                    ? "lightblue"
                    : "#ccc",
                  color: "#fff",
                }}
              >
                {framework.name}
              </button>
            ))}
          </div>
        );
      })}

      {/* Display selected versions */}
      {selectedFrameworks.map((selectedFramework) => {
        const language = programmingLanguages.find((lang) =>
          lang.frameworks.some((fw) => fw.name === selectedFramework)
        );
        const framework = language.frameworks.find(
          (fw) => fw.name === selectedFramework
        );

        return (
          <div key={selectedFramework}>
            <h3>Versions for {selectedFramework}</h3>
            {framework.versions.map((version) => (
              <button
                key={version}
                onClick={() => handleVersionClick(version)}
                style={{
                  marginRight: "10px",
                  marginBottom: "10px",
                  backgroundColor: selectedVersions.includes(version)
                    ? "lightblue"
                    : "#ccc",
                  color: "#fff",
                }}
              >
                {version}
              </button>
            ))}
          </div>
        );
      })}

      {/* Display selected libraries */}
      {selectedFrameworks.map((selectedFramework) => {
        const language = programmingLanguages.find((lang) =>
          lang.frameworks.some((fw) => fw.name === selectedFramework)
        );
        const framework = language.frameworks.find(
          (fw) => fw.name === selectedFramework
        );

        return (
          <div key={selectedFramework}>
            <h3>Libraries for {selectedFramework}</h3>
            {framework.libraries.map((library) => (
              <button
                key={library}
                onClick={() => handleLibraryClick(library)}
                style={{
                  marginRight: "10px",
                  marginBottom: "10px",
                  backgroundColor: selectedLibraries.includes(library)
                    ? "lightblue"
                    : "#ccc",
                  color: "#fff",
                }}
              >
                {library}
              </button>
            ))}
          </div>
        );
      })}

<button type="submit" disabled={loading} onClick={handleSubmit}>
  Generate and Download
</button>

</form>
<p className="response-area">{loading ? 'loading...' : response}</p>

   </div>

  );
};

export default App;
