import {useState} from 'react';
import axios from 'axios';
import CGLogo from './chatGPT.png';
import AppLogo from './app-logo.png';
import './App.css';
import fs from 'fs';
function App() {
  const [prompt, setPrompt] = useState('');
  const [response, setResponse] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);

    // communicate with API
    // post input value 'prompt' to API end point 
    axios
    .post('http://localhost:5555/chat', { prompt })
    .then((res) => {
      setResponse(res.data);
      setLoading(false);

      // Parse the response and create a project folder
      const projectFolder = './project';
      fs.mkdirSync(projectFolder);

      // Assuming the response is a string containing the project code
      const projectCode = res.data;

      // Write the project code to a file in the project folder
      const codeFile = `${projectFolder}/project.js`;
      fs.writeFileSync(codeFile, projectCode);

      setResponse('Project code received and saved as project.js');
    })
    .catch((err) => {
      console.error(err);
    });
};


  return (
    <div className="wrapper">
      <img src={AppLogo} alt="" className="app-logo" />	
      <form onSubmit={handleSubmit}>
        <img src={CGLogo} alt="" className={loading ? 'cg-logo loading' : 'cg-logo'} />
        <input
          type="text"
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          placeholder="Ask anything... :)"
        />
        <button type="submit">Ask</button>
      </form>
      <p className="response-area">{loading ? 'loading...' : response}</p>
      <div className="footer">~ webstylepress ~</div>
</div>
  );
}

export default App;
