import { useState } from 'react';
import axios from 'axios';
import CGLogo from './chatGPT.png';
import AppLogo from './app-logo.png';
import './App.css';

const App = () => {
  const [inputs, setInputs] = useState([]);
  const [selectedLanguages, setSelectedLanguages] = useState([]);
  const [selectedFrameworks, setSelectedFrameworks] = useState([]);
  const [selectedVersions, setSelectedVersions] = useState([]);
  const [selectedLibraries, setSelectedLibraries] = useState([]);
  const [selectedProfile, setSelectedProfile] = useState("");

  const [prompt, setPrompt] = useState('');

  const [response, setResponse] = useState('');
  const [loading, setLoading] = useState(false);


  const handleAddInput = () => {
    setInputs([...inputs, ""]);
  };

  const handleDeleteInput = (index) => {
    const updatedInputs = [...inputs];
    updatedInputs.splice(index, 1);
    setInputs(updatedInputs);
  };

  const handleInputChange = (index, value) => {
    const updatedInputs = [...inputs];
    updatedInputs[index] = value;
    setInputs(updatedInputs);
  };

  const handleLanguageClick = (language) => {
    if (selectedLanguages.includes(language)) {
      setSelectedLanguages(
        selectedLanguages.filter((lang) => lang !== language)
      );
      setSelectedFrameworks([]);
      setSelectedVersions([]);
      setSelectedLibraries([]);
    } else {
      setSelectedLanguages([...selectedLanguages, language]);
    }
  };

  const handleFrameworkClick = (framework) => {
    if (selectedFrameworks.includes(framework)) {
      setSelectedFrameworks(
        selectedFrameworks.filter((fw) => fw !== framework)
      );
      setSelectedVersions([]);
      setSelectedLibraries([]);
    } else {
      setSelectedFrameworks([...selectedFrameworks, framework]);
    }
  };

  const handleVersionClick = (version) => {
    if (selectedVersions.includes(version)) {
      setSelectedVersions(selectedVersions.filter((ver) => ver !== version));
    } else {
      setSelectedVersions([...selectedVersions, version]);
    }
  };

  const handleLibraryClick = (library) => {
    if (selectedLibraries.includes(library)) {
      setSelectedLibraries(selectedLibraries.filter((lib) => lib !== library));
    } else {
      setSelectedLibraries([...selectedLibraries, library]);
    }
  };

  const handleProfileSelection = (profile) => {
    setSelectedProfile(profile);
  };


  const updatePromptValue = () => {
    const promptValue = `Act as a highly experienced and seasoned :${selectedProfile} in the field .Build a: ${inputs.slice(0)}. Your goal is to generate a code handling the following tasks: ${inputs.slice(1).join(', ')} in: ${selectedLanguages.join(', ')} using: ${selectedFrameworks.join(', ')} with :${selectedVersions.join(', ')} and ${selectedLibraries.join(', ')}.
Then, act as a bug detector and review your code for security vulnerabilities and locate any logic errors or resource leaks and add the fixes needed.
Then, act as a code reviewer and analyze the whole previous provided code for code smells and assess the test coverage and add improvements to your code.
Then, act as a bug detector and review my code for security vulnerabilities and locate any logic errors and add the fixes needed.
Responses should not be translations of my input, but code written in the language I specified. Do not write explanations in your reply. Keep in mind that it is important to be concise, specific, and straight to the point.`;
    setPrompt(promptValue);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);

    // communicate with API
    // post input value 'prompt' to API end point 
    axios
      .post("http://localhost:5555/chat", { prompt })
      .then((res) => {
        setResponse(res.data);
        setLoading(false);
      })
      .catch((err) => {
        console.error(err);
      });
  };

  const programmingLanguages = [
    {
      name: "JavaScript",
      frameworks: [
        {
          name: "React",
          versions: ["17.0.2", "16.13.1"],
          libraries: ["React-DOM", "Redux"],
        },
        {
          name: "Angular",
          versions: ["12.0.0", "11.2.14"],
          libraries: ["RxJS", "NgRx"],
        },
      ],
    },
    {
      name: "Python",
      frameworks: [
        {
          name: "Django",
          versions: ["3.2.4", "2.2.24"],
          libraries: ["Django REST Framework", "Celery"],
        },
        {
          name: "Flask",
          versions: ["2.0.1", "1.1.4"],
          libraries: ["Flask-RESTful", "SQLAlchemy"],
        },
      ],
    },
    {
      name: "Java",
      frameworks: [
        {
          name: "Spring Boot",
          versions: ["2.5.0", "2.4.6"],
          libraries: ["Spring Data JPA", "Spring Security"],
        },
        {
          name: "Hibernate",
          versions: ["5.5.3", "5.4.31"],
          libraries: ["Java Persistence API (JPA)", "C3P0"],
        },
      ],
    },
  ];

  return (
    <div className="wrapper">
      <img src={AppLogo} alt="" className="app-logo" />
      <form onSubmit={handleSubmit}>
        <img src={CGLogo} alt="" className={loading ? 'cg-logo loading' : 'cg-logo'} />
        <input
          type="text"
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          placeholder="Ask anything... :)"
        />
              {/* Your additional code here */}
      <div>
        {/* Display inputs */}
        {inputs.map((input, index) => (
          <div key={index} style={{ display: "flex", alignItems: "center" }}>
            <input
              type="text"
              value={input}
              onChange={(e) => handleInputChange(index, e.target.value)}
              style={{ marginRight: "10px" }}
            />
            <button onClick={() => handleDeleteInput(index)}>-</button>
          </div>
        ))}

        <button onClick={handleAddInput}>+</button>
      </div>

      <div>
        {/* Display programming languages */}
        <h3>Programming Languages</h3>
        {programmingLanguages.map((language) => (
          <button
            key={language.name}
            onClick={() => handleLanguageClick(language.name)}
            style={{
              marginRight: "10px",
              marginBottom: "10px",
              backgroundColor: selectedLanguages.includes(language.name)
                ? "lightblue"
                : "#ccc",
              color: "#fff",
            }}
          >
            {language.name}
          </button>
        ))}
      </div>

      {/* Display selected frameworks */}
      {selectedLanguages.map((selectedLanguage) => {
        const language = programmingLanguages.find(
          (lang) => lang.name === selectedLanguage
        );

        return (
          <div key={selectedLanguage}>
            <h3>Frameworks for {selectedLanguage}</h3>
            {language.frameworks.map((framework) => (
              <button
                key={framework.name}
                onClick={() => handleFrameworkClick(framework.name)}
                style={{
                  marginRight: "10px",
                  marginBottom: "10px",
                  backgroundColor: selectedFrameworks.includes(framework.name)
                    ? "lightblue"
                    : "#ccc",
                  color: "#fff",
                }}
              >
                {framework.name}
              </button>
            ))}
          </div>
        );
      })}

      {/* Display selected versions */}
      {selectedFrameworks.map((selectedFramework) => {
        const language = programmingLanguages.find((lang) =>
          lang.frameworks.some((fw) => fw.name === selectedFramework)
        );
        const framework = language.frameworks.find(
          (fw) => fw.name === selectedFramework
        );

        return (
          <div key={selectedFramework}>
            <h3>Versions for {selectedFramework}</h3>
            {framework.versions.map((version) => (
              <button
                key={version}
                onClick={() => handleVersionClick(version)}
                style={{
                  marginRight: "10px",
                  marginBottom: "10px",
                  backgroundColor: selectedVersions.includes(version)
                    ? "lightblue"
                    : "#ccc",
                  color: "#fff",
                }}
              >
                {version}
              </button>
            ))}
          </div>
        );
      })}

      {/* Display selected libraries */}
      {selectedFrameworks.map((selectedFramework) => {
        const language = programmingLanguages.find((lang) =>
          lang.frameworks.some((fw) => fw.name === selectedFramework)
        );
        const framework = language.frameworks.find(
          (fw) => fw.name === selectedFramework
        );

        return (
          <div key={selectedFramework}>
            <h3>Libraries for {selectedFramework}</h3>
            {framework.libraries.map((library) => (
              <button
                key={library}
                onClick={() => handleLibraryClick(library)}
                style={{
                  marginRight: "10px",
                  marginBottom: "10px",
                  backgroundColor: selectedLibraries.includes(library)
                    ? "lightblue"
                    : "#ccc",
                  color: "#fff",
                }}
              >
                {library}
              </button>
            ))}
          </div>
        );
      })}

      {/* Send Inputs button */}
      <button onClick={updatePromptValue}>Send </button>
      <button type="submit">download</button>
      </form>
      <p className="response-area">{loading ? 'loading...' : response}</p>
      <div className="footer">~ webstylepress ~</div>


    </div>
  );
};

export default App;
