import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
  const [inputs, setInputs] = useState([]);
  const [selectedLanguages, setSelectedLanguages] = useState([]);
  const [selectedFrameworks, setSelectedFrameworks] = useState([]);
  const [selectedVersions, setSelectedVersions] = useState([]);
  const [selectedLibraries, setSelectedLibraries] = useState([]);
  const [selectedProfile, setSelectedProfile] = useState('');
  const [prompt, setPrompt] = useState('');
  const [response, setResponse] = useState('');
  const [loading, setLoading] = useState(false);

  const handleAddInput = () => {
    setInputs([...inputs, '']);
  };

  const handleDeleteInput = (index) => {
    const updatedInputs = [...inputs];
    updatedInputs.splice(index, 1);
    setInputs(updatedInputs);
  };

  const handleInputChange = (index, value) => {
    const updatedInputs = [...inputs];
    updatedInputs[index] = value;
    setInputs(updatedInputs);
  };

  const handleLanguageClick = (language) => {
    if (selectedLanguages.includes(language)) {
      setSelectedLanguages(selectedLanguages.filter((lang) => lang !== language));
      setSelectedFrameworks([]);
      setSelectedVersions([]);
      setSelectedLibraries([]);
    } else {
      setSelectedLanguages([...selectedLanguages, language]);
    }
  };

  const handleFrameworkClick = (framework) => {
    if (selectedFrameworks.includes(framework)) {
      setSelectedFrameworks(selectedFrameworks.filter((fw) => fw !== framework));
      setSelectedVersions([]);
      setSelectedLibraries([]);
    } else {
      setSelectedFrameworks([...selectedFrameworks, framework]);
    }
  };

  const handleVersionClick = (version) => {
    if (selectedVersions.includes(version)) {
      setSelectedVersions(selectedVersions.filter((ver) => ver !== version));
    } else {
      setSelectedVersions([...selectedVersions, version]);
    }
  };

  const handleLibraryClick = (library) => {
    if (selectedLibraries.includes(library)) {
      setSelectedLibraries(selectedLibraries.filter((lib) => lib !== library));
    } else {
      setSelectedLibraries([...selectedLibraries, library]);
    }
  };

  const handleProfileSelection = (profile) => {
    setSelectedProfile(profile);
  };

  const updatePromptValue = () => {
    const promptValue = `Act as a highly experienced and seasoned: ${selectedProfile} in the field. Build a: ${inputs.slice(0)}. Your goal is to generate a code handling the following tasks: ${inputs.slice(1).join(
      ', '
    )} in: ${selectedLanguages.join(', ')} using: ${selectedFrameworks.join(
      ', '
    )} with: ${selectedVersions.join(', ')} and ${selectedLibraries.join(
      ', '
    )}.
    Then, act as a bug detector and review your code for security vulnerabilities and locate any logic errors or resource leaks and add the fixes needed.
    Then, act as a code reviewer and analyze the whole previous provided code for code smells and assess the test coverage and add improvements to your code.
    Then, act as a bug detector and review my code for security vulnerabilities and locate any logic errors and add the fixes needed.
    Responses should not be translations of my input, but code written in the language I specified. Do not write explanations in your reply. Keep in mind that it is important to be concise, specific, and straight to the point.`;
    setPrompt(promptValue);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);

    // communicate with API
    // post prompt and get the response
    axios
      .post('API_ENDPOINT_URL', { prompt })
      .then((res) => {
        setResponse(res.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error(error);
        setLoading(false);
      });
  };

  const handleDownload = () => {
    // Code to download the response as a file
    const element = document.createElement('a');
    const file = new Blob([response], { type: 'text/plain' });
    element.href = URL.createObjectURL(file);
    element.download = 'api_response.txt';
    document.body.appendChild(element);
    element.click();
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <h2>Programming Languages:</h2>
        <ul>
          <li
            className={selectedLanguages.includes('JavaScript') ? 'selected' : ''}
            onClick={() => handleLanguageClick('JavaScript')}
          >
            JavaScript
          </li>
          <li
            className={selectedLanguages.includes('Python') ? 'selected' : ''}
            onClick={() => handleLanguageClick('Python')}
          >
            Python
          </li>
          <li
            className={selectedLanguages.includes('Java') ? 'selected' : ''}
            onClick={() => handleLanguageClick('Java')}
          >
            Java
          </li>
          {/* Add more programming languages */}
        </ul>

        <h2>Frameworks:</h2>
        <ul>
          <li
            className={selectedFrameworks.includes('React') ? 'selected' : ''}
            onClick={() => handleFrameworkClick('React')}
          >
            React
          </li>
          <li
            className={selectedFrameworks.includes('Angular') ? 'selected' : ''}
            onClick={() => handleFrameworkClick('Angular')}
          >
            Angular
          </li>
          <li
            className={selectedFrameworks.includes('Vue') ? 'selected' : ''}
            onClick={() => handleFrameworkClick('Vue')}
          >
            Vue
          </li>
          {/* Add more frameworks */}
        </ul>

        <h2>Versions:</h2>
        <ul>
          <li
            className={selectedVersions.includes('v1') ? 'selected' : ''}
            onClick={() => handleVersionClick('v1')}
          >
            v1
          </li>
          <li
            className={selectedVersions.includes('v2') ? 'selected' : ''}
            onClick={() => handleVersionClick('v2')}
          >
            v2
          </li>
          <li
            className={selectedVersions.includes('v3') ? 'selected' : ''}
            onClick={() => handleVersionClick('v3')}
          >
            v3
          </li>
          {/* Add more versions */}
        </ul>

        <h2>Libraries:</h2>
        <ul>
          <li
            className={selectedLibraries.includes('Axios') ? 'selected' : ''}
            onClick={() => handleLibraryClick('Axios')}
          >
            Axios
          </li>
          <li
            className={selectedLibraries.includes('Lodash') ? 'selected' : ''}
            onClick={() => handleLibraryClick('Lodash')}
          >
            Lodash
          </li>
          <li
            className={selectedLibraries.includes('Moment') ? 'selected' : ''}
            onClick={() => handleLibraryClick('Moment')}
          >
            Moment
          </li>
          {/* Add more libraries */}
        </ul>

        <h2>Profiles:</h2>
        <select value={selectedProfile} onChange={(e) => handleProfileSelection(e.target.value)}>
          <option value="">Select a profile</option>
          <option value="Developer">Developer</option>
          <option value="Engineer">Engineer</option>
          <option value="Architect">Architect</option>
        </select>

        <h2>Main Task:</h2>
        <input type="text" value={inputs[0]} onChange={(e) => handleInputChange(0, e.target.value)} />

        <h2>Additional Functionalities:</h2>
        {inputs.slice(1).map((input, index) => (
          <div key={index}>
            <input
              type="text"
              value={input}
              onChange={(e) => handleInputChange(index + 1, e.target.value)}
            />
            <button type="button" onClick={() => handleDeleteInput(index + 1)}>
              Delete
            </button>
          </div>
        ))}
        <button type="button" onClick={handleAddInput}>
          Add Functionality
        </button>

        <button type="submit">Send</button>
      </form>

      {loading && <p>Loading...</p>}
      {response && (
        <div>
          <h2>API Response:</h2>
          <p>{response}</p>
          <button onClick={handleDownload}>Download Response</button>
        </div>
      )}
    </div>
  );
};

export default App;
